import React from 'react';
import {UsersType} from "../../redux/users-reduser";
import s from "./Users.module.css"
import {NavLink} from "react-router-dom";


type UsersPropsType = {
    users: UsersType[]
    totalUsersCount: number
    pageSize: number
    currentPage: number
    followHandler: (userId: number) => void
    unFollowHandler: (userId: number) => void
    clickNextPage: (pageNumber: number) => void
}

class UsersClassComponent extends React.Component<UsersPropsType> {

    onPageChanged = (el: number) => {
        this.props.clickNextPage(el)
    }

    render() {
        let pagesCount = Math.ceil(this.props.totalUsersCount / this.props.pageSize)
        let pages = []
        for (let i = 1; i <= pagesCount; i++) {
            pages.push(i)
        }

        return <div className={s.main_container_users}>
            <div className={s.usersPagesNumber}>{pages.map((el) => (
                <span
                    className={this.props.currentPage === el ? s.pageSelected : ""}
                    onClick={() => {
                        this.onPageChanged(el)
                    }}>
                    {el}
                </span>))}
            </div>
            {this.props.users.map((el: UsersType) => {
                return <div key={el.id} className={s.userContainer}>
                        <span className={s.container_img}>
                            <div>
                                <NavLink to={`/profile/${el.id}`}>
                                    <img
                                        src={el.photos.small !== null ? el.photos.small : "data:image/png;base64,"}
                                        alt={"photo user"} className={s.userPhoto}/>
                                </NavLink>
                            </div>
                        <div>
                            {el.followed
                                ? <button onClick={() => {
                                    this.props.unFollowHandler(el.id)
                                }}>Unfollow</button>
                                : <button onClick={() => {
                                    this.props.followHandler(el.id)
                                }}>Follow</button>
                            }
                        </div>
                            </span>
                    <span className={s.nameLocation_Container}>
                            <span className={s.userContainer_name}>
                                <div>{el.name}</div>
                                <div>{el.status}</div>
                            </span>
                            <span className={s.userContainer_location}>
                                <div>No country</div>
                                <div>No city</div>
                            </span>
                        </span>
                </div>

            })}
        </div>
    }
}

export default UsersClassComponent;